// app/Controllers/UserController.js
BaseController.extend('UserController', {
    // List all users
    list: {
        dataSource: 'storage/data/users.json',

        transform(users) {
            return users.map(user => ({
                ...user,
                fullName: `${user.firstName} ${user.lastName}`,
                isActive: user.status === 'active'
            }));
        },

        afterLoad(users) {
            console.log(`Loaded ${users.length} users`);
        }
    },

    // View single user
    detail: {
        dataSource(params) {
            return `storage/data/users/${params.id}.json`;
        },

        transform(user) {
            return {
                ...user,
                avatar: user.photo || '/public/media/default-avatar.jpg',
                joinedDate: new Date(user.created_at).toLocaleDateString()
            };
        }
    }
});

BaseController.extend('ProductController', {
    list: {
        dataSource: 'storage/data/products.json',

        // Transform data before setting to state
        transform(products) {
            return products.filter(p => p.inStock);
        },

        // Called after data is loaded and transformed
        afterLoad(products) {
            document.title = `Products (${products.length})`;

            // Track analytics
            if (window.gtag) {
                gtag('event', 'page_view', { page_title: 'Products' });
            }
        },

        // Called if loading fails
        onError(error) {
            console.error('Failed to load products:', error);
            Framework.usePlugin('toast', 'Failed to load products', 'error');
        }
    }
});

BaseController.extend('DivisionController', {
    detail: {
        dataSource(params) {
            return `storage/data/divisions/${params.slug}.json`;
        },

        transform(division) {
            return {
                ...division,

                // Transform members array
                members: division.members?.map(member => ({
                    ...member,
                    photoUrl: member.photo || '/storage/media/default.jpg',
                    instagramUrl: `https://instagram.com/${member.instagram.replace('@', '')}`
                })) || [],

                // Transform programs array
                programs: division.programs?.map(program => ({
                    ...program,
                    statusClass: this.getStatusClass(program.status),
                    dateRange: this.formatDateRange(program.start_date, program.end_date)
                })) || []
            };
        },

        // Helper methods
        getStatusClass(status) {
            const classes = {
                planned: 'bg-yellow-100 text-yellow-800',
                ongoing: 'bg-blue-100 text-blue-800',
                completed: 'bg-green-100 text-green-800'
            };
            return classes[status] || 'bg-gray-100 text-gray-800';
        },

        formatDateRange(start, end) {
            if (!start) return '';
            if (!end || start === end) return start;
            return `${start} - ${end}`;
        }
    }
});

