// app/Controllers/PostController.js
BaseController.extend('PostController', {
    // List all posts
    list: {
        dataSource: 'storage/data/posts.json',

        transform(posts) {
            return posts.map(post => ({
                ...post,
                excerpt: post.content.substring(0, 150) + '...',
                publishedDate: new Date(post.created_at).toLocaleDateString(),
                author: post.author || 'Anonymous'
            }));
        }
    },

    // View single post
    detail: {
        dataSource(params) {
            return `storage/data/posts/${params.id}.json`;
        },

        transform(post) {
            return {
                ...post,
                formattedDate: new Date(post.created_at).toLocaleDateString(),
                readTime: Math.ceil(post.content.split(' ').length / 200) + ' min read'
            };
        },

        afterLoad(post) {
            document.title = `${post.title} - Blog`;
        }
    }
});
